mapper.delete:
DELETE FROM his.constant


        AND his.constant.constant_id= #{constant_id}


        AND constant_type = #{constant_type} AND constant_name = #{constant_name}

mapper.update:
update department
set department_id = #{department_id},
set department_name = #{department_name},
set department_cat = #{department_cat},
set department_type = #{department_type}
where department_id = #{department_id}
